//Desestructuraci贸n de objetos

//Asignaci贸n desestructurante

const persona = {
    nombre: 'Tony',
    edad: 45,
    clave: 'Ironman',
};

const { nombre, edad, clave } = persona;

console.log(persona.nombre);
console.log(persona.edad);
console.log(persona.clave);

console.log(nombre);
console.log(edad);
console.log(clave);

const retornaPersona = ( usuario ) => {
    console.log(usuario);
};

retornaPersona()
