import { heroes } from './data/heroes';

console.log( heroes );


const getHeroeById = (id) => {
    // return heroes.find(heroe => heroe.id === id);
    return heroes.find( (heroe) => {
        if ( heroe.id === id ) {
        return true;    
        }else {
            return false;
        }
    });
}

console.log( getHeroeById(2) );
