//Desestructuraci贸n de objetos

//Asignaci贸n desestructurante

const persona = {
    nombre: 'Tony',
    edad: 45,
    clave: 'Ironman',
};

const { nombre, edad, clave } = persona;

console.log(persona.nombre);
console.log(persona.edad);
console.log(persona.clave);

console.log(nombre);
console.log(edad);
console.log(clave);

const retornaPersona = ( usuario ) => {
    console.log(usuario);
};


const retornaPersona2 = ( {nombre, edad, clave} ) => {
    console.log(edad, clave, nombre);
};


const retornaPersona3 = ( {nombre, edad, clave, rango= 'capitan'} ) => {
    console.log(edad, clave, nombre, rango);
};

retornaPersona( persona );
retornaPersona2( persona );
retornaPersona3( persona );

const content = ( {nombre, edad, clave, rango= 'capitan'} ) => {
    return {
        nombreClave: clave,
        anios: edad

    }
};

const avenger = content( persona );
console.log( avenger );

const {nombreClave, anios} = content( persona );
console.log( nombreClave, anios );
