import { getHeroeById } from './bases/08-imp-ext'; 


//Promesas son async

const promesa = new Promise( (resolve, reject) => {

    setTimeout( () => {
        const heroe = getHeroeById(2);
        console.log(heroe);
        resolve(heroe);
    },2000)

});

//correcta
promesa.then( () => {
    console.log( 'then de la promesa');
})
