//Funciones

function getSaludo (nombre) {
    return `Hola, ${nombre}`;
}

console.log( getSaludo('pablo'));



//Otro tipo en constante

const saludar = function( nombre ) {
    return `Hola caracola ${nombre}`;
}

console.log( saludar );


const salurdar2 = ( nombre )  => {
    return `Hola caracola ${nombre}`;
}

console.log( saludar);
