import { getHeroeById } from './bases/08-imp-ext'; 


//Promesas son async

const promesa = new Promise( (resolve, reject) => {

    setTimeout( () => {
        const heroe = getHeroeById(2);
        reject( 'no se pudo encontrar el heroe' );
    },2000)

});

//correcta
promesa.then( (heroe) => {
    console.log( heroe );
})
